mapper.select:
select count(*) from board


	where subject like #{ keyword }



	where writer like #{ keyword }

select num, writer, email, subject, passwd, reg_date, readcount, ref, re_step, re_level, content, ip from (select rownum as rank, num, writer, email, subject, passwd, reg_date, readcount, ref, re_step, re_level, content, ip from (select num, writer, email, subject, passwd, reg_date, readcount, ref, re_step, re_level, content, ip from board order by ref desc, re_step asc ))
where writer like #{ keyword }
where subject like #{ keyword }
